import classes from './AvailableMeals.module.css'
import Card from "../UI/Card";
import MealItem from "./MealItems/MealItem";
const DUMMY_MEALS = [
    {
        id: 'm1',
        name: 'Dosa',
        description: 'Finest Dosa with excellent chutney.',
        price: 40,
    },
    {
        id: 'm2',
        name: 'Vada',
        description: 'Exquisite taste!',
        price: 30,
    },
    {
        id: 'm3',
        name: 'Idly',
        description: 'As smooth as cotton.',
        price: 30,
    },
    {
        id: 'm4',
        name: 'Poori',
        description: 'A tasty delight.',
        price: 40,
    },
];

const AvailableMeals=()=>{
    const mealsList=DUMMY_MEALS.map(meal=><MealItem id={meal.id} key={meal.id} name={meal.name} description={meal.description} price={meal.price}/>)
    return <section className={classes.meals}>
       <Card> <ul>
           {mealsList}
       </ul>
       </Card>

    </section>

}


export default AvailableMeals
